s oe=0 
f {
	s oe=$o(^OEORD(0,"Adm",admid,oe))
	q:oe=""
	s oei=0
	f {
		s oei=$o(^OEORD(oe,"I",oei))
		q:oei=""
		s (con,contact)=""
