s event=##class(User.RBEvent).%OpenId(eventid)
i event {
	s ^||ADIS("Events",eventid,"I179")=$zstrip(event.EVNumber,"*PA")

	i event.EVHospitalDR {
		s TRUSTCode=""
		s x=0
		f x=1:1:event.EVHospitalDR.ChildCTHospitalTrusts.Count() {
