Method.Implementation:
; method takes property NAME of type annotation and returns width/height of background image
n (%request, %session, obj, ImgProperty)
s OEAnnotID=""
try{
	; annotationID is stored in property WITHOUT the '_HREF' suffix
	s OEAnnotID=$property(obj,$p(ImgProperty,"_HREF"))
} catch(err){}
s oOEAnnot=##Class(User.OEAnnotation).%OpenId(OEAnnotID)
s (H, W)=150
i oOEAnnot {
	s oPic=""
	if oOEAnnot.ANNOTMRPicDR,oOEAnnot.ANNOTMRPicDR.PICMRCPictDR s oPic=oOEAnnot.ANNOTMRPicDR.PICMRCPictDR.PICStreamData
	if 'oPic,oOEAnnot.ANNOTMRPicDR s oPic=oOEAnnot.ANNOTMRPicDR.PICwebsysDocument
	if 'oPic,oOEAnnot.ANNOTPersonPicturesDR s oPic=oOEAnnot.ANNOTPersonPicturesDR.PICwebsysDocument
	if oPic,$isobject(oPic),oPic.docData.Size {
		d ##Class(websys.UI.Annotate.Utils).GetImageDimensions(oPic.docData,.W,.H)
	}
}
;re-scale large images to ensure they can fit on A4 size
if W > 450 { s W=450 }
if H > 600 { s H=600 }
q W_"px,"_H_"px"

; method takes a property NAME of type GlobalBinaryStream
n (%request, %session, obj, ImgProperty)
s oSrcStream=""
s (H, W)=150
try{
	s oSrcStream=$property(obj,ImgProperty)
} catch(err){}
i oSrcStream, oSrcStream.Size  {
	s W=oSrcStream.GetAttribute("ImgWidth"),H=oSrcStream.GetAttribute("ImgHeight")
}
;re-scale large images to ensure they can fit on A4 size
if W > 450 { s W=450 }
if H > 600 { s H=600 }
q W_"px,"_H_"px"
