Method.Implementation:
k ^zbernard // Get reportid i.e. use $INCREMENT to add another node to ^CacheTemp global. // We use ^CacheTemp global because it will always use memory before disk New repid,ind
// Use $Increment to get the next node Set repid=$I(^CacheTemp) s ^zBernard("1")=(1)
// Build data into ^CacheTemp(ReportID) // e.g. Set ^CacheTemp(ReportID,Index)=$LB(f1,f2,f3) New Number,String,Date,Time,c,n New PAPMINo,PAADMADMNo,PAPMIName,PAPMIName2,PAADMType,PAADMAdmDate,PAADMAdmTime,PAADMDischgDate,PAADMDischgTime, PAADMHospitalDR,HOSPDesc,CareTypeCode,CareTpyeDesc,DchTypeCode,FINCode,WardCode,BedCode,RoomCode,LOS,MthBedDays s (PAADMHospitalDR,LOS,MthBedDays)=0 s (PAPMINo,PAADMADMNo,PAPMIName,PAPMIName2,PAADMType,PAADMAdmDate,PAADMAdmTime,PAADMDischgDate,PAADMDischgTime,HOSPDesc, CareTypeCode,CareTpyeDesc,DchTypeCode,FINCode,WardCode,BedCode,RoomCode)=""
s ind=1
//Convert ODBC dates to cache format s LDofMth=$$intdate^SSUTIL4(LDofMth)
//s ^bernard("2")=(2)
&sql(declare SWARHRATD1014 cursor for SELECT DISTINCT PAPMI_No, PAADM_ADMNo, {fn UCASE(%EXACT(PAPMI_Name))}, UCASE(PAPMI_Name2), PAADm_Type, PAADM_AdmDate, PAADM_AdmTime, PAADM_DischgDate, PAADM_DischgTime, PAADM_Hospital_DR, PAADM_Hospital_DR->HOSP_Desc, PAADM_MainMRADM_DR->MRADM_CareType_DR->CareTyp_Code As CareTypeCode, PAADM_MainMRADM_DR->MRADM_CareType_DR->CareTyp_Desc As CareTpyeDesc, PAADM_MainMRADM_DR->MRADM_DischType_DR->CTDSP_Code As DchTypeCode, INS_InsType_DR->INST_Code As FINCode, PAADM_CurrentWard_DR->Ward_Code As WardCode, PAADM_CurrentBed_DR->BED_Code As BedCode, PAADM_CurrentBed_DR->BED_Room_DR->Room_Code As RoomCode, --(CASE WHEN datediff("d",PAADM_AdmDate,:LDofMth)=0 THEN 1 ELSE datediff("d",PAADM_AdmDate,:LDofMth) END) As LOS, --(CASE WHEN DateDiff("m",PAADM_AdmDate,:LDofMth)=0 THEN (CASE WHEN datediff("d",PAADM_AdmDate,:LDofMth)=0 -- THEN 1 ELSE datediff("d",PAADM_AdmDate,:LDofMth) END) ELSE ({fn DAYOFMONTH(:LDofMth)} -1)END) As MthBedDays datediff("d",PAADM_AdmDate,:LDofMth) + 1 As LOS, (CASE WHEN DateDiff("m",PAADM_AdmDate,:LDofMth)=0 THEN (datediff("d",PAADM_AdmDate,:LDofMth)) ELSE ({fn DAYOFMONTH(:LDofMth)} -1)END) + 1 As MthBedDays
FROM SQLUser.PA_Adm A LEFT OUTER JOIN SQLUser.PA_PatMas B ON A.PAADM_PAPMI_DR = B.PAPMI_RowId LEFT OUTER JOIN SQLUser.PA_AdmInsurance C ON A.PAADM_RowID = C.INS_ParRef
WHERE PAADM_Hospital_DR = :Hospital AND PAADM_AdmDate <= :LDofMth AND (PAADM_DischgDate > :LDofMth OR PAADM_DischgDate IS NULL) AND PAADM_Type = 'I' AND PAADM_VisitStatus not in ('C','P') AND PAADM_MainMRADM_DR->MRADM_CareType_DR->CareTyp_Code <> '' AND (UCASE(PAADM_MainMRADM_DR->MRADM_CareType_DR->CareTyp_Code) BETWEEN UCASE(:SCTCode) AND UCASE(:ECTCode) OR (:SCTCode IS NULL)) )
&sql(open SWARHRATD1014)
//fetch first row &sql(fetch SWARHRATD1014 into :PAPMINo,:PAADMADMNo,:PAPMIName,:PAPMIName2,:PAADMType,:PAADMAdmDate,:PAADMAdmTime,:PAADMDischgDate,:PAADMDischgTime, :PAADMHospitalDR,:HOSPDesc,:CareTypeCode,:CareTpyeDesc,:DchTypeCode,:FINCode,:WardCode,:BedCode,:RoomCode,:LOS,:MthBedDays)
while 'SQLCODE{
d FormatDates d FormatTimes
// build the row Set ^CacheTemp(repid,ind)=$LB(PAPMINo,PAADMADMNo,PAPMIName,PAPMIName2,PAADMType,PAADMAdmDate,PAADMAdmTime,PAADMDischgDate,PAADMDischgTime, PAADMHospitalDR,HOSPDesc,CareTypeCode,CareTpyeDesc,DchTypeCode,FINCode,WardCode,BedCode,RoomCode,LOS,MthBedDays) s ind=ind+1 //fetch rows &sql(fetch SWARHRATD1014 into :PAPMINo,:PAADMADMNo,:PAPMIName,:PAPMIName2,:PAADMType,:PAADMAdmDate,:PAADMAdmTime,:PAADMDischgDate,:PAADMDischgTime, :PAADMHospitalDR,:HOSPDesc,:CareTypeCode,:CareTpyeDesc,:DchTypeCode,:FINCode,:WardCode,:BedCode,:RoomCode,:LOS,:MthBedDays) }
//s ^zBernard("3")=(3) &sql(close SWARHRATD1014)
// Build QHandle (AtEnd,ReportID,Index) Set QHandle=$lb(0,repid,0)
//s ^zBernard("4")=(4)
Quit $$$OK
FormatDates // Convert the dates to ODBC date format If PAADMAdmDate Set PAADMAdmDate=$ZD(PAADMAdmDate,3) If PAADMDischgDate Set PAADMDischgDate=$ZD(PAADMDischgDate,3) Quit
FormatTimes // Convert the times to ODBC time format If PAADMAdmTime Set PAADMAdmTime=$ZT(PAADMAdmTime,1) If PAADMDischgTime Set PAADMDischgTime=$ZT(PAADMDischgTime,1) Quit

// This fetch method should never have to change. 

// repid - Report ID
// ind   - sequence index which represents each row

New repid,ind

// Restore QHandle
Set AtEnd=$li(QHandle,1)
Set repid=$li(QHandle,2)
Set ind=$li(QHandle,3)

Set ind=$o(^CacheTemp(repid,ind))
If ind="" {	// if there are no more rows, finish fetching
Set AtEnd=1
Set Row=""
}
Else      {	// fetch row
Set Row=^CacheTemp(repid,ind)
}

// Save QHandle
s QHandle=$lb(AtEnd,repid,ind)

Quit $$$OK
