Method.Implementation:

 k ^zbernard	
 // Get reportid i.e. use $INCREMENT to add another node to ^CacheTemp global. 
 // We use ^CacheTemp global because it will always use memory before disk
 New repid,ind

 // Use $Increment to get the next node
 Set repid=$I(^CacheTemp)
 s ^zBernard("1")=(1)

 // Build data into ^CacheTemp(ReportID)
 // e.g. Set ^CacheTemp(ReportID,Index)=$LB(f1,f2,f3)
 New Number,String,Date,Time,c,n
 n PAPMINo,PAPMIDeceasedDate,QDateFirstRecur,QRegionFirstRecur,QCauseDeath,QHOSPITAL
 s (QHOSPITAL)=0
 s (PAPMINo,PAPMIDeceasedDate,QDateFirstRecur,QRegionFirstRecur,QCauseDeath)=""

 s ind=1

 //Convert ODBC dates to cache format
 s DateFrom=$$intdate^SSUTIL4(DateFrom)
 s DateTo=$$intdate^SSUTIL4(DateTo)

 //s ^bernard("2")=(2)

 &sql(declare VCONPART3 cursor for

 select distinct
questionnaire.QVCONPART3.QUESPAPatMasDR->PAPMI_No,
questionnaire.QVCONPART3.QUESPAPatMasDR->PAPMI_Deceased_Date,
QDateFirstRecur,
QRegionFirstRecur,
QCauseDeath,
questionnaire.QVCONPART3.QHOSPITAL

 from questionnaire.QVCONPART3,questionnaire.QVCONPART1,SQLUser.PA_PatMas
 where questionnaire.QVCONPART1.QFirstPDDT between :DateFrom AND :DateTo
AND questionnaire.QVCONPART3.QHOSPITAL=:Hospital
AND questionnaire.QVCONPART1.QUESPAPatMasDR=questionnaire.QVCONPART3.QUESPAPatMasDR
and (PAPMI_Deceased_Date is not null
or QDateFirstRecur is not null
or QRegionFirstRecur is not null
or QCauseDeath is not null)
and PAPMI_RowId=questionnaire.QVCONPART3.QUESPAPatMasDR


)

 &sql(open VCONPART3)

  //fetch first row 
 &sql(fetch VCONPART3 into :PAPMINo,:PAPMIDeceasedDate,:QDateFirstRecur,:QRegionFirstRecur,:QCauseDeath,:QHOSPITAL
 )

 while 'SQLCODE{

 d FormatDates

 // build the row
 Set ^CacheTemp(repid,ind)=$LB(PAPMINo,PAPMIDeceasedDate,QDateFirstRecur,QRegionFirstRecur,QCauseDeath,QHOSPITAL)
 s ind=ind+1
 //fetch rows
 &sql(fetch VCONPART3 into :PAPMINo,:PAPMIDeceasedDate,:QDateFirstRecur,:QRegionFirstRecur,:QCauseDeath,:QHOSPITAL
 )
 }

 //s ^zBernard("3")=(3)
 &sql(close VCONPART3)

 // Build QHandle (AtEnd,ReportID,Index)
 Set QHandle=$lb(0,repid,0)

 //s ^zBernard("4")=(4)

 Quit $$$OK


FormatDates
 // Convert the dates to ODBC date format
 If PAPMIDeceasedDate Set PAPMIDeceasedDate=$ZD(PAPMIDeceasedDate,3)
 If QDateFirstRecur Set QDateFirstRecur=$ZD(QDateFirstRecur,3)
 Quit

// This fetch method should never have to change. 

// repid - Report ID
// ind   - sequence index which represents each row

New repid,ind

// Restore QHandle
Set AtEnd=$li(QHandle,1)
Set repid=$li(QHandle,2)
Set ind=$li(QHandle,3)

Set ind=$o(^CacheTemp(repid,ind))
If ind="" {	// if there are no more rows, finish fetching
Set AtEnd=1
Set Row=""
}
Else      {	// fetch row
Set Row=^CacheTemp(repid,ind)
}

// Save QHandle
s QHandle=$lb(AtEnd,repid,ind)

Quit $$$OK
