Method.Implementation:

 k ^zbernard 
 // Get reportid i.e. use $INCREMENT to add another node to ^CacheTemp global. 
 // We use ^CacheTemp global because it will always use memory before disk
 New repid,ind

 // Use $Increment to get the next node
 Set repid=$I(^CacheTemp)
 s ^zBernard("1")=(1)

 // Build data into ^CacheTemp(ReportID)
 // e.g. Set ^CacheTemp(ReportID,Index)=$LB(f1,f2,f3)
 New Number,String,Date,Time,c,n
 n PAADMAdmNo,VSTDesc,PAADMAdmDate,PAADMAdmTime,TimeFrameNum,PAADMDischgDate,PAADMDischgTime,PAADMPriorityDR,MRADMDischClassifDR,HOSPRowId,HOSPDesc
 s (HOSPRowId,PAADMPriorityDR,MRADMDischClassifDR)=0
 s (PAADMAdmNo,VSTDesc,PAADMAdmDate,PAADMAdmTime,TimeFrameNum,PAADMDischgDate,PAADMDischgTime,HOSPDesc)=""

 s ind=1

 //Convert ODBC dates to cache format
 s DateFrom=$$intdate^SSUTIL4(DateFrom)
 s DateTo=$$intdate^SSUTIL4(DateTo)

 //s ^bernard("2")=(2)

  if (SortType="discharge") {

 &sql(declare SHED073 cursor for
 SELECT
 PAADM_AdmNo,
 PAADM_MainMRADM_DR->MRADM_VisitType_DR->VST_Desc,
 PAADM_AdmDate,
 PAADM_AdmTime,
 convert(integer,convert(integer,PAADM_AdmTime)/1800) as TimeFrameNum,
 PAADM_DischgDate,
 PAADM_DischgTime,
 PAADM_Priority_DR,
 PAADM_MainMRADM_DR->MRADM_DischClassif_DR,
 PAADM_CurrentWard_DR->WARD_LocationDR->CTLOC_HOspital_DR->HOSP_RowId,
 PAADM_CurrentWard_DR->WARD_LocationDR->CTLOC_HOspital_DR->HOSP_Desc
FROM SQLUser.PA_Adm
WHERE PAADM_DischgDate between :DateFrom and :DateTo AND PAADM_VisitStatus<>'C' 
AND PAADM_Hospital_DR=:Hospital
AND PAADM_Type='E')
 }

 if (SortType="admission") {

 &sql(declare SHED073adm cursor for
 SELECT
 PAADM_AdmNo,
 PAADM_MainMRADM_DR->MRADM_VisitType_DR->VST_Desc,
 PAADM_AdmDate,
 PAADM_AdmTime,
 convert(integer,convert(integer,PAADM_AdmTime)/1800)as TimeFrameNum,
 PAADM_DischgDate,
 PAADM_DischgTime,
 PAADM_Priority_DR,
 PAADM_MainMRADM_DR->MRADM_DischClassif_DR,
 PAADM_CurrentWard_DR->WARD_LocationDR->CTLOC_HOspital_DR->HOSP_RowId,
 PAADM_CurrentWard_DR->WARD_LocationDR->CTLOC_HOspital_DR->HOSP_Desc
FROM SQLUser.PA_Adm
WHERE PAADM_AdmDate between :DateFrom and :DateTo AND PAADM_VisitStatus<>'C' 
AND PAADM_Hospital_DR=:Hospital
AND PAADM_Type='E')
 }

 if (SortType="discharge") {
 &sql(open SHED073) 

 //fetch first row
 &sql(fetch SHED073 into :PAADMAdmNo,:VSTDesc,:PAADMAdmDate,:PAADMAdmTime,:TimeFrameNum,:PAADMDischgDate,:PAADMDischgTime,:PAADMPriorityDR,:MRADMDischClassifDR,:HOSPRowId,:HOSPDesc)

 while 'SQLCODE{
 d FormatDates
 d FormatTimes
 If TimeFrameNum Set TimeFrameNum=$$num(TimeFrameNum,2)
 // build the row
 Set ^CacheTemp(repid,ind)=$LB(PAADMAdmNo,VSTDesc,PAADMAdmDate,PAADMAdmTime,TimeFrameNum,PAADMDischgDate,PAADMDischgTime,PAADMPriorityDR,MRADMDischClassifDR,HOSPRowId,HOSPDesc)
 s ind=ind+1
 //fetch rows
 &sql(fetch SHED073 into :PAADMAdmNo,:VSTDesc,:PAADMAdmDate,:PAADMAdmTime,:TimeFrameNum,:PAADMDischgDate,:PAADMDischgTime,:PAADMPriorityDR,:MRADMDischClassifDR,:HOSPRowId,:HOSPDesc)
 }
 &sql(close SHED073)
 }

  if (SortType="admission") {
 &sql(open SHED073adm) 

 //fetch first row
 &sql(fetch SHED073adm into :PAADMAdmNo,:VSTDesc,:PAADMAdmDate,:PAADMAdmTime,:TimeFrameNum,:PAADMDischgDate,:PAADMDischgTime,:PAADMPriorityDR,:MRADMDischClassifDR,:HOSPRowId,:HOSPDesc)

 while 'SQLCODE{
 d FormatDates
 d FormatTimes
 If TimeFrameNum Set TimeFrameNum=$$num(TimeFrameNum,2)
 // build the row
 Set ^CacheTemp(repid,ind)=$LB(PAADMAdmNo,VSTDesc,PAADMAdmDate,PAADMAdmTime,TimeFrameNum,PAADMDischgDate,PAADMDischgTime,PAADMPriorityDR,MRADMDischClassifDR,HOSPRowId,HOSPDesc)
 s ind=ind+1
 //fetch rows
 &sql(fetch SHED073adm into :PAADMAdmNo,:VSTDesc,:PAADMAdmDate,:PAADMAdmTime,:TimeFrameNum,:PAADMDischgDate,:PAADMDischgTime,:PAADMPriorityDR,:MRADMDischClassifDR,:HOSPRowId,:HOSPDesc)
 }
 &sql(close SHED073adm)
 }


 // Build QHandle (AtEnd,ReportID,Index)
 Set QHandle=$lb(0,repid,0)

 //s ^zBernard("4")=(4)
 Quit $$$OK

num(str,length) ;left pad with 0

 Q $E(str+(10**length),2,length+1)

 ;
FormatDates
 // Convert the dates to ODBC date format
 If PAADMAdmDate Set PAADMAdmDate=$ZD(PAADMAdmDate,3)
 If PAADMDischgDate Set PAADMDischgDate=$ZD(PAADMDischgDate,3)
 /*If PAADMTriageDate Set PAADMTriageDate=$ZD(PAADMTriageDate,3)
 If PAADMSeenDate Set PAADMSeenDate=$ZD(PAADMSeenDate,3)*/

 //s ^zBernard("5")=(5)
 Quit

FormatTimes
 // Convert the times to ODBC time format
 If PAADMAdmTime Set PAADMAdmTime=$ZT(PAADMAdmTime,2)
 /*$extract($ZT(PAADMAdmTime,2),0,2)_":00"*/
 If PAADMDischgTime Set PAADMDischgTime=$ZT(PAADMDischgTime,2)
 /*If PAADMTriageTime Set PAADMTriageTime=$ZT(PAADMTriageTime,2)
 If PAADMSeenTime Set PAADMSeenTime=$ZT(PAADMSeenTime,2)*/
 Quit

// This fetch method should never have to change. 

// repid - Report ID
// ind - sequence index which represents each row

New repid,ind

// Restore QHandle
Set AtEnd=$li(QHandle,1)
Set repid=$li(QHandle,2)
Set ind=$li(QHandle,3)

Set ind=$o(^CacheTemp(repid,ind))
If ind="" {        // if there are no more rows, finish fetching
Set AtEnd=1
Set Row=""
}
Else      {         // fetch row
Set Row=^CacheTemp(repid,ind)
}

// Save QHandle
s QHandle=$lb(AtEnd,repid,ind)
Quit $$$OK
